	a = a+b>=mod?a+b-mod:a+b;
}
ll bp(ll a, ll p) {
	int r = 1;
	while(p) {
		if(p&1) r = r*a%mod;
		a = a*a%mod, p>>=1;
	}
	return r;
} 
