            idx[i + 1]++; 
        if (s[i] == '<')
            idx[i + 1]--;
        if (s[i] == '+')
            Hash[i + 1] += val[n + idx[i + 1]]; 
        if (s[i] == '-')
            Hash[i + 1] -= val[n + idx[i + 1]]; 
    }

    for (int i = n; i >= 0; i--) {
        ull tmp = Hash[i] + Hash[n] * val[n + idx[i]];
        ans += Map[tmp]; 
        Map[ Hash[i] ]++;
    }

