  vector<ModInt<> > b(A);

  REP(c, 0, A) {
    do {
      b[c] = mt();
    } while (b[c].to_ll() == 0);
  }
  vector<vector<ModInt<> > > hsh(A, vector<ModInt<> >(n + 1));
  ModInt<> inv[A] = { 0 };
  REP(c, 0, A) inv[c] = b[c].pow(mod - 2);;
  REP(c, 0, A) {
    p = 0;
    ModInt<> cur = 1;
    REP(i, 0, n) {
      ModInt<> tmp = hsh[c][i];
      if (s[i] == '>') {
        p++;
        cur = cur * b[c];
      }
      if (s[i] == '<') {
        p--;
        cur = cur * inv[c];
      }
      if (s[i] == '+') {
        tmp = tmp + cur;
      }
      if (s[i] == '-') {
        tmp = tmp - cur;
      }
      hsh[c][i + 1] = tmp;
