inline ull power(ull x, ull y)
{
	ull Ans = 1;
	while(y)
	{
		if(y & 1)
			Ans *= x;
		y >>= 1;
		x *= x;
	}
	return Ans;
}
map <ull, ull> P;
int main()
{
	ull ny = power(key, k);
	scanf("%d%s", &n, s);
	ull w = 0;
	ull p = 0;
	for(int i = 0; i < n; ++ i)
	{
		if(s[i] == '+')
			++ P[p];
		else if(s[i] == '-')
			-- P[p];
		else if(s[i] == '>')
			++ p;
		else -- p;
	}
	for(int i = - n; i < 0; ++ i)
		w += power(ny, - i) * P[i];
	for(int i = 0; i <= n; ++ i)
		w += power(key, i) * P[i];
	ull a = 1, b = 0;
	ull Ans = 0;
	for(int i = n - 1; i >= 0; -- i)
	{
		++ M[-b * power(a, k)];
		if(s[i] == '+')
			++ b;
		else if(s[i] == '-')
			-- b;
		else if(s[i] == '>')
			a *= key, b *= key;
		else if(s[i] == '<')
			a *= ny, b *= ny;
		Ans += M[(w - b) * power(a, k)];
	}
	printf("%llu", Ans);
