LL inv2(LL x){return x==1?1:(mod2-mod2/x)*inv2(mod2%x)%mod2;}

int main(){
	scanf("%lld%s",&n,s+1);
	pw1[0]=ipw1[0]=1; iB1=inv1(B1=233);
	pw2[0]=ipw2[0]=1; iB2=inv2(B2=377);
	for (LL i=1;i<=n*2;++i){
		pw1[i]=pw1[i-1]*B1%mod1;
		ipw1[i]=ipw1[i-1]*iB1%mod1;
		pw2[i]=pw2[i-1]*B2%mod2;
		ipw2[i]=ipw2[i-1]*iB2%mod2;
	}
	p[0]=n;
	for (LL i=1;i<=n;++i){
		if (s[i]=='+'){
			p[i]=p[i-1];
			h1[i]=(h1[i-1]+pw1[p[i]])%mod1;
			h2[i]=(h2[i-1]+pw2[p[i]])%mod2;
		}
		if (s[i]=='-'){
			p[i]=p[i-1];
			h1[i]=(h1[i-1]-pw1[p[i]]+mod1)%mod1;
			h2[i]=(h2[i-1]-pw2[p[i]]+mod2)%mod2;
		}
		if (s[i]=='>'){
			p[i]=p[i-1]+1;
			h1[i]=h1[i-1]; h2[i]=h2[i-1];
		}
		if (s[i]=='<'){
			p[i]=p[i-1]-1;
			h1[i]=h1[i-1]; h2[i]=h2[i-1];
		}
		++mp[h1[i]][h2[i]];
	}
	for (LL i=1;i<=n;++i){
 		LL d=p[i-1]-p[0],t1=h1[n],t2=h2[n];
		if (d>=0){t1=t1*pw1[d]%mod1; t2=t2*pw2[d]%mod2;}
		else{t1=t1*ipw1[-d]%mod1; t2=t2*ipw2[-d]%mod2;}
		t1=(t1+h1[i-1]+mod1)%mod1; t2=(t2+h2[i-1]+mod2)%mod2;
		ans+=mp[t1][t2];
		--mp[h1[i]][h2[i]];
	}
	printf("%lld\n",ans);

	return 0;
}

