ll ppow(ll x,ll k){
	ll ans=1;
	for(;k;k>>=1,Mul(x,x))if (k&1)Mul(ans,x);
	return ans;
}
ll Query(ll l,ll r){
	memset(sum,0,sizeof sum);
	ll pos=2*n,zt=0;
	For(i,l,r){
		if (s[i]=='+')++sum[pos];
		lf (s[i]=='-')--sum[pos];
		lf (s[i]=='>')	++pos;
		else			--pos;
	}
	For(i,1,3*n)Add(zt,sum[i]*bin[i])%mod;
	Add(zt,mod);
	return zt;
}
void cdq(ll l,ll r){
	if (l>r)return ;
	if (l==r){
		if (s[l]=='+')answ+=bin[2*n]==start;
		lf (s[l]=='-')answ+=(mod-bin[2*n])%mod==start;
		return ;
	}
	mp.clear();
	ll mid=(l+r)>>1,ans=0,pos=2*n;
	FOr(i,mid,l){
		if (s[i]=='+')ans+=bin[pos];
		lf (s[i]=='-')ans-=bin[pos];
		lf (s[i]=='>')	--pos;
		else			++pos;
		if (pos<2*n)now[i]=ans*bin[2*n-pos]%mod,Pos[i]=Inv[2*n-pos];
		else		now[i]=ans*Inv[pos-2*n]%mod,Pos[i]=bin[pos-2*n];
	}
	pos=2*n;++mp[0];ans=0;
	For(i,mid+1,r){
		if (s[i]=='+')ans+=bin[pos];
		lf (s[i]=='-')ans-=bin[pos];
		lf (s[i]=='>')	++pos;
		else			--pos;
		Add(ans,mod);
		if (pos<2*n)++mp[ans];
		else		++mp[ans];
	}
//	writeln(Query(1,3)*);
	For(i,l,mid){
		answ+=mp[((start-now[i])*Pos[i]%mod+mod)%mod];
	}
	cdq(l,mid-1);	cdq(mid+1,r);
}
int main(){
	n=read();readstr(s+1);Inv_bas=ppow(bas,mod-2);
	bin[0]=1;For(i,1,4*n)bin[i]=bin[i-1]*bas%mod;
	Inv[0]=1;For(i,1,4*n)Inv[i]=Inv[i-1]*Inv_bas%mod;
	start=Query(1,n);
	cdq(1,n);
	writeln(answ);
}

