using vmn = vector<mn>;
using vvmn = vector<vmn>;

const int B = 6, MAXV = 2 * 250001;
const array<mn, B> bases = { 69, 420, 42069, 6969, 696969 };
using state = array<array<mn, 2>, 6>;

state IDENTITY, LSHIFT, RSHIFT, INCR, DECR;

void init() {
    F0R (b, B) {
        IDENTITY[b] = {1,0};
        LSHIFT[b] = {bases[b]+MAXV, 0};
        RSHIFT[b] = {1/(bases[b]+MAXV), 0};
        INCR[b] = {1,1};
        DECR[b] = {1,-1};
    }
}
const state& op(char c) {
    return c=='<' ? LSHIFT : c=='>' ? RSHIFT : c=='+' ? INCR : DECR;
}
const state& inv(char c) {
    return c=='<' ? RSHIFT : c=='>' ? LSHIFT : c=='+' ? DECR : INCR;
}

state compose(const state& f, const state& g) {
    state fog;
    F0R (b, B) fog[b] = {
        f[b][0] * g[b][0],
        f[b][1] + f[b][0] * g[b][1]
    };
    return fog;
}

