long long modinv(long long a, long long mod) {
    long long b = mod, u = 1, v = 0;
    while (b) {
        long long t = a/b;
        a -= t*b; swap(a, b);
        u -= t*v; swap(u, v);
    }
    u %= mod;
    if (u < 0) u += mod;
    return u;
}

long long modpow(long long a, long long n, long long mod) {
    long long res = 1;
    if (n < 0) {
        a = modinv(a, mod);
        n = -n;
        return modpow(a, n, mod);
    }
    while (n > 0) {
        if (n & 1) res = res * a % mod;
        a = a * a % mod;
        n >>= 1;
    }
    return res;
}

const long long MOD = 1000000009;
const long long BASE = 17;

long long solve(int N, const string &S) {
    vector<long long> hash(N+1, 0), pval(N+1, 0);
    map<long long, vector<int>> pos;

    for (int i = 0; i < N; ++i) {
        if (S[i] == '>') {
            hash[i+1] = hash[i];
            pval[i+1] = pval[i] + 1;
        }
        else if (S[i] == '<') {
            hash[i+1] = hash[i];
            pval[i+1] = pval[i] - 1;
        }
        else if (S[i] == '+') {
            pval[i+1] = pval[i];
            long long add = modpow(BASE, pval[i], MOD);
            hash[i+1] = (hash[i] + add) % MOD;
        }
        else if (S[i] == '-') {
            pval[i+1] = pval[i];
            long long add = modpow(BASE, pval[i], MOD);
            hash[i+1] = (hash[i] - add + MOD) % MOD;
        }
        pos[hash[i+1]].push_back(i+1);

        //cout << i+1 << ": " << hash[i+1] << ", " << pval[i+1] << endl;
    }

    long long res = 0;
    for (int i = 0; i <= N; ++i) {
        long long cur = hash[i];
        long long risou_add = hash[N] * modpow(BASE, pval[i], MOD) % MOD;
        long long risou = (cur + risou_add) % MOD;

        /*
        COUT("-----------------");
        COUT(i); COUT(pval[i]); COUT(pos[risou]);
        cout << cur << " + " << risou_add << " -> " << risou << endl;
        */

        auto v = pos[risou];
        int it = upper_bound(v.begin(), v.end(), i) - v.begin();
        res += (int)v.size() - it;
    }
    return res;
}

int main() {
    int N; string S;
    while (cin >> N >> S) cout << solve(N, S) << endl;
}

