const ll M=19260817,mo=998244353;
int n,i,j,k,a[maxn];
ll s[maxn],c[maxn],mul[maxn],inv[maxn],invM,ans;
map<int,ll> S; 
char ch;

ll ksm(ll x,ll y){
	ll s=1;
	for(;y;y/=2,x=x*x%mo) if (y&1)
		s=s*x%mo;
	return s;
}

ll cm(int k){return (k<0)?inv[-k]:mul[k];}

int main(){
	scanf("%d",&n),ch=getchar();
	while (ch!='<'&&ch!='>'&&ch!='+'&&ch!='-') ch=getchar();
	for(i=1;i<=n;i++) a[i]=ch,ch=getchar();
	mul[0]=1;for(i=1;i<=n;i++) mul[i]=mul[i-1]*M%mo;
	inv[0]=1,invM=ksm(M,mo-2);for(i=1;i<=n;i++) inv[i]=inv[i-1]*invM%mo; 
	for(i=n;i>=1;i--) {
		c[i]=c[i+1];
		if (a[i]=='<') s[i]=s[i+1]*M%mo,c[i]++; else 
		if (a[i]=='>') s[i]=s[i+1]*invM%mo,c[i]--; else 
		if (a[i]=='+') s[i]=(s[i+1]+1)%mo; else s[i]=(s[i+1]+mo-1)%mo;
	}
//	for(i=1;i<=n;i++) for(j=i+1;j<=n+1;j++)
