//			ans++;
//	printf("%lld\n",ans),ans=0;
	S[0]=1;
	for(i=n;i>=1;i--) {
		ll v=(s[i]-s[1]+mo)*cm(-c[i])%mo;
		if (S.find(v)!=S.end()) 
			ans+=S[v];
		S[s[i]*cm(-c[i])%mo]++;
	}
	printf("%lld",ans);
}

#include<bits/stdc++.h>
using namespace std;

#define rep(i,n) for(int i=0;i<(n);i++)
#define REP(i,n) for(int i=1;i<=(n);i++)
#define mp make_pair
#define pb push_back
#define fst first
#define snd second
typedef long long ll;
typedef pair<int,int> pii;

const int maxn=500005;
const int hval[4]={12345678,23456781,34567812,45678123};
const int mod[4]={515880193,998244353,int(1e9+7),int(1e9+9)};
class Hash{
public:
	int h[4];
	Hash(){memset(h,0,sizeof(h));}
	bool operator < (const Hash &o)const{
		if(h[0]!=o.h[0])return h[0]<o.h[0];
		if(h[1]!=o.h[1])return h[1]<o.h[1];
		if(h[2]!=o.h[2])return h[2]<o.h[2];
		return h[3]<o.h[3];
	}
	Hash operator + (const Hash &o)const{
		Hash res;
		rep(i,4){
			res.h[i]=h[i]+o.h[i];
			if(res.h[i]>=mod[i])res.h[i]-=mod[i];
		}
		return res;
	}
	Hash operator - (const Hash &o)const{
		Hash res;
		rep(i,4){
			res.h[i]=h[i]-o.h[i];
			if(res.h[i]<0)res.h[i]+=mod[i];
		}
		return res;
	}
	Hash operator * (const Hash &o)const{
		Hash res;
		rep(i,4)res.h[i]=1LL*h[i]*o.h[i]%mod[i];
		return res;
	}
}H,iH,one,pw[maxn],ipw[maxn],sum[maxn];
int n;
int pos[maxn];
map<Hash,int>cnt;
char s[maxn];

int power(int x,int pow,int mod){
	int res=1;
	for(;pow;pow>>=1){
		if(pow&1)res=1LL*res*x%mod;
		x=1LL*x*x%mod;
	}
	return res;
}

int main(){
	scanf("%d%s",&n,s+1);
	rep(i,4){
		H.h[i]=hval[i];
		iH.h[i]=power(hval[i],mod[i]-2,mod[i]);
		one.h[i]=1;
	}
	pw[0]=ipw[0]=one;
	REP(i,2*n){
		pw[i]=pw[i-1]*H;
		ipw[i]=ipw[i-1]*iH;
	}
	REP(i,n){
		pos[i]=pos[i-1];
		sum[i]=sum[i-1];
		if(s[i]=='<')pos[i]--;
		else if(s[i]=='>')pos[i]++;
		else if(s[i]=='+')sum[i]=sum[i]+pw[pos[i]+n];
		else sum[i]=sum[i]-pw[pos[i]+n];
	}
	ll ans=0;
	for(int i=n;i>=1;i--){
		cnt[sum[i]]++;
		if(pos[i-1]<0)ans+=cnt[(sum[n]*ipw[-pos[i-1]])+sum[i-1]];
		else ans+=cnt[(sum[n]*pw[pos[i-1]])+sum[i-1]];
	}
	printf("%lld\n",ans);
	return 0;
}

#include<bits/stdc++.h>
using namespace std;
typedef long long ll;
const int N=300005,p=998244353;
int read(){
	int f=1,g=0;
	char ch=getchar();
	for (;!isdigit(ch);ch=getchar()) if (ch=='-') f=-1;
	for (;isdigit(ch);ch=getchar()) g=g*10+ch-'0';
	return f*g;
}
int qpow(int x,int k){
	int t=1;
	for (;k;k>>=1){
		if (k&1) t=(ll)t*x%p;
		x=(ll)x*x%p;
	}
	return t;
}
struct state{
	int a[6];
	int &operator[](int i){return a[i];}
	state(){memset(a,0,sizeof(a));}
}x,rx,s[N][2],c,e;
bool operator==(state a,state b){
	for (int i=0;i<6;i++)
	if (a[i]==b[i]) return false;
	return true;
}
bool operator<(state a,state b){
	for (int i=0;i<6;i++)
	if (a[i]!=b[i]) return a[i]<b[i];
	return false;
}
state operator*(state x,state y){for (int i=0;i<6;i++) x[i]=(ll)x[i]*y[i]%p;return x;}
state operator+(state x,state y){for (int i=0;i<6;i++) if((x[i]+=y[i])>=p) x[i]-=p;return x;}
state operator-(state x,state y){for (int i=0;i<6;i++) if((x[i]-=y[i])<0) x[i]+=p;return x;}
state operator+(state x,int k){for (int i=0;i<6;i++) if ((x[i]+=k)>=p) x[i]-=p;return x;}
state operator-(state x,int k){for (int i=0;i<6;i++) if ((x[i]-=k)<0) x[i]+=p;return x;}
state getval(state *a,state x){
	state cur=a[0]+a[1]*x;
	return cur;
}
int n,m;
ll ans;
char ch[N];
map<state,int> f;
int main(){
	for (int i=0;i<6;i++) x[i]=i+1,rx[i]=qpow(x[i],p-2);
	n=read();
	scanf(" %s",ch+1);
	for (int i=0;i<6;i++) s[n+1][1][i]=1;
	for (int i=n;i;i--)
	if (ch[i]=='+'){s[i][0]=s[i+1][0]+s[i+1][1];s[i][1]=s[i+1][1];c=c-1;}
	else if (ch[i]=='-'){s[i][0]=s[i+1][0]-s[i+1][1];s[i][1]=s[i+1][1];c=c+1;}
	else if (ch[i]=='>'){s[i][0]=s[i+1][0];s[i][1]=s[i+1][1]*x;c=c*rx;}
	else if (ch[i]=='<'){s[i][0]=s[i+1][0];s[i][1]=s[i+1][1]*rx;c=c*x;}
	for (int i=n;i;i--){
		f[getval(s[i+1],e)]++;
		ans+=f[getval(s[i],c)];
	}
	printf("%lld\n",ans);
	return 0;
}

