	}
	for(LL i=1;i<=n;i++)
	{
		if(s[i]=='<') p--;if(s[i]=='>') p++;
		if(s[i]=='-') t[p+n]--;if(s[i]=='+') t[p+n]++;
	}
	for(int k=0;k<4;k++)
		for(LL i=-n;i<=n;i++) (c[k]+=pre[k][i+n]*t[i+n]%mod)%=mod;
	Pre();
	for(int i=0;i<4;i++) solve(a[i],0,i);
	for(int i=0;i<4;i++) solve(b[i],c[i],i);
	LL ans=0;
	//printf("c:%lld\n",c);
	//for(int i=1;i<=n;i++) printf("op:%lld %lld\n",op[i].a,op[i].b);
