	{
		mp[MP(MP(b[0][i-1],b[1][i-1]),MP(b[2][i-1],b[3][i-1]))]++;
		ans+=mp[MP(MP(a[0][i],a[1][i]),MP(a[2][i],a[3][i]))];
	}
	printf("%lld",ans);
}

