template<typename T> bool get_int(T &x)
{
	char t=getchar();
	bool neg=false;
	x=0;
	for(; (t>'9'||t<'0')&&t!='-'&&t!=EOF; t=getchar());
	if(t=='-')neg=true,t=getchar();if(t==EOF)return false;
	for(; t<='9'&&t>='0'; t=getchar())x=x*10+t-'0';
	if(neg)x=-x;return true;
}
template<typename T> void print_int(T x)
{
	if(x<0)putchar('-'),x=-x;
	short a[20]= {},sz=0;
	while(x>0)a[sz++]=x%10,x/=10;
	if(sz==0)putchar('0');
	for(int i=sz-1; i>=0; i--)putchar('0'+a[i]);
}
#define ff first
#define ss second
#define pb push_back
#define mp make_pair
#define get1(a) get_int(a)
#define get2(a,b) (get1(a)&&get1(b))
#define get3(a,b,c) (get1(a)&&get2(b,c))
#define printendl(a) print_int(a),puts("")
typedef long long LL;
typedef unsigned long long uLL;
typedef pair<int,int> pii;
const int inf=0x3f3f3f3f;
const LL Linf=1ll<<61;
const double pi=acos(-1.0);

int qpow(int x,int y,int mod)
{
	int ret=1;
	while(y)
	{
		if(y&1)ret=(LL)ret*x%mod;
		x=(LL)x*x%mod;
		y>>=1;
	}
	return ret;
}

const int maxn=250111;
const int mod1=998244353;
const int mod2=1e9+7;
const int base=443;
const int inv1=qpow(base,mod1-2,mod1);
const int inv2=qpow(base,mod2-2,mod2);

pii merge1(const pii&a,const pii&b){return mp(1ll*a.ff*b.ff%mod1,(1ll*a.ff*b.ss+a.ss)%mod1);}
pii merge2(const pii&a,const pii&b){return mp(1ll*a.ff*b.ff%mod2,(1ll*a.ff*b.ss+a.ss)%mod2);}
pair<pii,pii> merge(const pair<pii,pii>&a,const pair<pii,pii>&b){return mp(merge1(a.ff,b.ff),merge2(a.ss,b.ss));}
int eval1(const int&a,const pii&b){return ((LL)a*b.ff+b.ss)%mod1;}
int eval2(const int&a,const pii&b){return ((LL)a*b.ff+b.ss)%mod2;}
pii eval(const pii&a,const pair<pii,pii>&b){return mp(eval1(a.ff,b.ff),eval2(a.ss,b.ss));}

int n;
pair<pii,pii> tran[maxn];
char s[maxn];

int main()
{
	scanf("%d%s",&n,s+1);

	pii cur=mp(0,0);
	tran[n+1]=mp(mp(1,0),mp(1,0));
	for(int i=n;i>=1;i--)
	{
		if(s[i]=='+')
		{
			tran[i]=merge(tran[i+1],mp(mp(1,1),mp(1,1)));
			cur=eval(cur,mp(mp(1,mod1-1),mp(1,mod2-1)));
		}
		else if(s[i]=='-')
		{
			tran[i]=merge(tran[i+1],mp(mp(1,mod1-1),mp(1,mod2-1)));
			cur=eval(cur,mp(mp(1,1),mp(1,1)));
		}
		else if(s[i]=='<')
		{
			tran[i]=merge(tran[i+1],mp(mp(base,0),mp(base,0)));
			cur=eval(cur,mp(mp(inv1,0),mp(inv2,0)));
		}
		else
		{
			tran[i]=merge(tran[i+1],mp(mp(inv1,0),mp(inv2,0)));
			cur=eval(cur,mp(mp(base,0),mp(base,0)));
		}
	}

	LL ans=0;
	map<pii,int> cnt;
	cnt[mp(0,0)]++;
	for(int i=n;i>=1;i--)
	{
		ans+=cnt[eval(cur,tran[i])];
		cnt[mp(tran[i].ff.ss,tran[i].ss.ss)]++;
	}
	printendl(ans);
	return 0;
}

