int n;
char str[maxn + 1];
int delta[maxn + 1], sum[maxn + 1];

int main(void) {
  scanf("%d%s", &n, str);
  Init(n);
  delta[0] = 0;
  sum[0] = 0;
  for (int i = 1; i <= n; ++i) {
    switch (str[i - 1]) {
    case '+':
      delta[i] = delta[i - 1];
      sum[i] = Add(sum[i - 1] + pow[delta[i]]);
      break;
    case '-':
      delta[i] = delta[i - 1];
      sum[i] = Sub(sum[i - 1] - pow[delta[i]]);
      break;
    case '<':
      delta[i] = delta[i - 1] - 1;
      sum[i] = sum[i - 1];
      break;
    case '>':
      delta[i] = delta[i - 1] + 1;
      sum[i] = sum[i - 1];
      break;
    }
  }
  unordered_map<int, int> cnt;
  li ans = 0;
  for (int i = n; i >= 0; --i) {
    if (i < n) {
      int target = Add(sum[i] + Mul(pow[delta[i]], sum[n]));
      ans += cnt[target];
    }
    ++cnt[sum[i]];
  }
  printf("%lld\n", ans);
  return 0;
}

