const int pmod = 1e9 + 7;
const int qmod = 1e9 + 9;

struct hash_t {
  int p, q;
  hash_t() {

  }
  hash_t(int x, int y) : p(x), q(y) {
  } 

  bool operator < (const hash_t &b) const {
    return make_pair(p, q) < make_pair(b.p, b.q);
  }
};

void exgcd(int a, int b, int &x, int &y) {
  if (!b)
    x = 1, y = 0;
  else
    exgcd(b, a % b, y, x), y -= (a / b) * x;
}

int mod_inv(int a, int p) {
  int x, y;
  exgcd(a, p, x, y);
  assert((ll) (x % p + p) % p * a % p == 1);
  return (x % p + p) % p;
}
hash_t operator + (hash_t x, hash_t y) {
  return hash_t((x.p + y.p) % pmod, (x.q + y.q) % qmod);
}
hash_t operator - (hash_t x, hash_t y) {
  return hash_t((x.p + pmod - y.p) % pmod, (x.q + qmod - y.q) % qmod);
} 
hash_t operator * (hash_t x, hash_t y) {
  return hash_t((ll) x.p * y.p % pmod, (ll) x.q * y.q % qmod);
}
hash_t operator / (hash_t x, hash_t y) {
  return hash_t((ll) x.p * mod_inv(y.p, pmod) % pmod, (ll) x.q * mod_inv(y.q, qmod) % qmod);
}

