		if(it == '+') phas1 += pow(p).fi, phas2 += pow(p).se;
		else if(it == '-') phas1 -= pow(p).fi, phas2 -= pow(p).se;
		else if(it == '>') p++;
		else p--;
	}

	phas1 %= MOD1; if(phas1 < 0) phas1 += MOD1;
	phas2 %= MOD2; if(phas2 < 0) phas2 += MOD2;

	ll ans = 0;
	M[(phas1 << 32) | phas2]++;
    p = 0;

	for(auto it : S) {
		if(it == '+') chas1 = (chas1 + pow(p).fi) % MOD1, chas2 = (chas2 + pow(p).se) % MOD2;
		else if(it == '-') chas1 = (chas1 - pow(p).fi + MOD1) % MOD1, chas2 = (chas2 - pow(p).se + MOD2) % MOD2;
		else if(it == '>') p++;
		else p--;

		ans += M[(chas1 << 32) | chas2];

		ll has1 = (chas1 + pow(p).fi * phas1) % MOD1;
		ll has2 = (chas2 + pow(p).se * phas2) % MOD2;

		M[(has1 << 32) | has2]++;
	}

	cout<<ans<<"\n";

	return 0;
}

