const int maxN = 250011;
const ll mod = 1000000007;
const ll base = 500167;

ll poww(ll a, ll b) {
    ll ans = 1;
    while (b > 0) {
        if (b & 1) ans = (ans * a) % mod;
        b >>= 1;
        a = (a * a) % mod;
    }
    return ans;
}

struct smart_map {
    map<ll, ll> M;
    ll bonus;

    smart_map() {
        M.clear();
        bonus = 1;
    }

    void operator*=(ll v) {
        bonus = (bonus * v) % mod;
    }

    void add(ll x) {
        x = (x * poww(bonus, mod - 2)) % mod;
        M[x]++;
    }

    ll query(ll x) {
        x = (x * poww(bonus, mod - 2)) % mod;
        return M[x];
    }
};

ll n, i, act, full;
char s[maxN];
smart_map data;
const ll inv_base = poww(base, mod - 2);
ll ans;

int main()
{
    //freopen("test.in","r",stdin);

    scanf("%d\n%s", &n, s + 1);

    data.add(0);
    act = 0;

    for (i = n; i >= 1; i--) {
        if (s[i] == '+') act = (act + 1) % mod;    
        if (s[i] == '-') act = (act + mod - 1) % mod;
        if (s[i] == '<') act = (act * base) % mod;
        if (s[i] == '>') act = (act * inv_base) % mod;
    }

    full = act;
    act = 0;

    for (i = n; i >= 1; i--) {
        if (s[i] == '+') act = (act + 1) % mod;    
        if (s[i] == '-') act = (act + mod - 1) % mod;
        if (s[i] == '<') act = (act * base) % mod, data *= base;
        if (s[i] == '>') act = (act * inv_base) % mod, data *= inv_base;

        ll src = (mod + act - full) % mod;
        ans += data.query(src);

        data.add(act);
    }

    printf("%lld", ans);

    return 0;
}

