#define mul0 10009
struct RollingHash {
	int n, sz, res; vector<int> hash;
    RollingHash(int _n) : n(_n), sz(2*n+2), res(0){
        hash.resize(sz+1,0), hash[0] = 1;
		rep(i,sz) hash[i+1]=((ll)hash[i]*mul0)%MOD;
    }
    int calc(int id, bool pl){
        if(pl) res = add(res, hash[id+n+1]);
        else res = sub(res, hash[id+n+1]);
        return res;
    }
};

