  typedef pair<Int_h,Int_h>key;
  static Hash pows[MAX_N];

  key a;
  static void init(){
    M1=2,M2=3;
    rev1=Int_h(1)/M1;
    rev2=Int_h(1)/M2;

    Int_h* ar[4]={mul1,mul2,div1,div2};
    Int_h mult[4]={M1,M2,rev1,rev2};
    REP(j,4){
      ar[j][0]=1;
      REP(i,MAX_N-1){
        ar[j][i+1]=ar[j][i]*mult[j];
      }
    }
    REP(i,MAX_N) pows[i].a=mp(mul1[i],mul2[i]);
  }
  Hash(lint val=0){
    a=mp(val,val);
  }
  Hash &operator+=(const Hash& b) { 
    a.fr+=b.a.fr;
    a.sc+=b.a.sc;

    return *this;
  }
  Hash &operator-=(const Hash& b) {
    a.fr-=b.a.fr;
    a.sc-=b.a.sc;

    return *this;
  }
  Hash &operator <<=(int k){
    if(k<0) (*this)>>=(-k);
    else{
      a.fr*=mul1[k];
      a.sc*=mul2[k];
      return *this;
    }
  }
  Hash &operator >>=(int k){
    if(k<0) (*this)<<=(-k);
    else{
      a.fr*=div1[k];
      a.sc*=div2[k];
      return *this;
    }
  }
  Hash &operator =(Hash b){
    a=b.a;
    return *this;
  }
  Hash &operator =(lint val){
    a.fr=val;
    a.sc=val;
    return *this;
  }
  Hash operator -()const{
    return Hash(0)-=Hash(*this);
  }

  Hash operator +(const Hash& b) const{
    return Hash(*this)+=b;
  }
  Hash operator -(const Hash& b) const{
    return Hash(*this)-=b;
  }

  Hash operator <<(int k) const{
    return Hash(*this)<<=k;
  }
  Hash operator >>(int k) const{
    return Hash(*this)>>=k;
  }

  bool operator==(const Hash& b) const { return (a==b.a); }
  bool operator!=(const Hash& b) const { return (a!=b.a); }
  bool operator<(const Hash& b) const {
    return mp(a.fr.x,a.sc.x)<mp(b.a.fr.x,b.a.sc.x);
  }
};

