int BinPow(int x, int m, int mod) {
    if(m == 0)
        return 1;
    else if(m == 1)
        return x % mod;
    else {
        int sum = BinPow(x, m >> 1, mod);
        sum = (1ll * sum * sum) % mod;
        if(m % 2)
            sum = (1ll * sum * x) % mod;
        return sum;
    }
}

int main() {
    cin.tie(0), ios::sync_with_stdio(0);
    cin >> n;
    cin >> s;
    s = "+" + s;
    pw[0][0] = pw[1][0] = 1;
    for(int i = 1; i <= 2 * N - 10; i++) {
        pw[0][i] = (1ll * pw[0][i - 1] * B) % mod1;
        pw[1][i] = (1ll * pw[1][i - 1] * B) % mod2;
    }
    for(int i = 1; i <= n; i++) {
        lmao[i] = cnt;
        if(s[i] == '+') {
            hs[0][i] = (1ll * hs[0][i - 1] + pw[0][cnt]) % mod1;
            hs[1][i] = (1ll * hs[1][i - 1] + pw[1][cnt]) % mod2;
        }
        if(s[i] == '-') {
            hs[0][i] = (1ll * hs[0][i - 1] - pw[0][cnt]) % mod1;
            hs[1][i] = (1ll * hs[1][i - 1] - pw[1][cnt]) % mod2;
            hs[0][i] = (1ll * hs[0][i] + mod1) % mod1;
            hs[1][i] = (1ll * hs[1][i] + mod2) % mod2;
        }
        if(s[i] == '>'){
            cnt++;
            hs[0][i] = (1ll * hs[0][i - 1] + mod1) % mod1;
            hs[1][i] = (1ll * hs[1][i - 1] + mod2) % mod2;
        }
        if(s[i] == '<'){
            cnt--;
            hs[0][i] = (1ll * hs[0][i - 1] + mod1) % mod1;
            hs[1][i] = (1ll * hs[1][i - 1] + mod2) % mod2;
        }
    }
    int x1 = (1ll * hs[0][n] * BinPow(pw[0][lmao[1]], mod1 - 2, mod1)) % mod1;
    int x2 = (1ll * hs[1][n] * BinPow(pw[1][lmao[1]], mod2 - 2, mod2)) % mod2;
    type = {x1, x2};
    for(int i = n; i >= 1; i--) {
        mymap[{hs[0][i], hs[1][i]}]++;
        x1 = ((1ll * type.fi * pw[0][lmao[i]]) % mod1 + 1ll * hs[0][i - 1]) % mod1;
        x2 = ((1ll * type.se * pw[1][lmao[i]]) % mod2 + 1ll * hs[1][i - 1]) % mod2;
        sum += mymap[{x1, x2}];
    }
    cout << sum << "\n";
}

