constexpr int inv(int a, int m) {
  return a == 1 ? 1 : 1LL * (m - m / a) * inv(m % a, m) % m;
}

const int BASE = 1000003;
const int M = 2;
const int MODS[] = {1000000007, 1000000009};

// template <int BASE, int... INTS> struct HashT {
struct HashT {
  // static constexpr int M = sizeof...(INTS);
  // static constexpr std::array<int, M> MODS = {INTS...};

  explicit HashT(int a) {
    for (int i = 0; i < M; ++i) {
      h[i] = a;
    }
  }

  explicit HashT(const HashT &a, const HashT &b) {
    for (int i = 0; i < M; ++i) {
      h[i] = 1LL * a.h[i] * b.h[i] % MODS[i];
    }
  }

  explicit HashT(const HashT &k, const HashT &b, const HashT &bf) {
    for (int i = 0; i < M; ++i) {
      h[i] = 1LL * k.h[i] * (b.h[i] + MODS[i] - bf.h[i]) % MODS[i];
    }
  }

  template <int a> void add() {
    for (int i = 0; i < M; ++i) {
      h[i] += a >= 0 ? a : MODS[i] + a;
      if (h[i] >= MODS[i]) {
        h[i] -= MODS[i];
      }
    }
  }

  void multiplyB() {
    for (int i = 0; i < M; ++i) {
      h[i] = 1LL * h[i] * BASE % MODS[i];
    }
  }

  void divideB() {
    for (int i = 0; i < M; ++i) {
      h[i] = 1LL * h[i] * inv(BASE, MODS[i]) % MODS[i];
    }
  }

  int h[M];
};

// template <int BASE, int... INTS>
// bool operator<(const HashT<BASE, INTS...> &a, const HashT<BASE, INTS...> &b) {
