//   for (int i = 0; i < HashT<BASE, INTS...>::M; ++i) {
bool operator<(const HashT &a, const HashT &b) {
  for (int i = 0; i < M; ++i) {
    if (a.h[i] != b.h[i]) {
      return a.h[i] < b.h[i];
    }
  }
  return false;
}

