int fpow(int x, int y) {
	int ans = 1;
	while (y) {
		if (y & 1) ans = 1ll * ans * x % mod;
		y >>= 1; x = 1ll * x * x % mod;
	}
	return ans;
}

struct data {
	int k[6], b[6];

	ll getans() {
		ll x = 0, y = 0;
		for (int i = 0; i < 6; ++i) {
			int w = 1ll * (res[i] - b[i] + mod) * fpow(k[i], mod - 2) % mod;
			if (i < 3)
				(x += 1ll * w * (i + 1) % mod) %= mod;
			else
				(y += 1ll * w * (i + 1) % mod) %= mod;
		}
		return x | (y << 32ll);
	}

	ll hsh() {
		ll x = 0, y = 0;
		for (int i = 0; i < 6; ++i) {
			if (i < 3) 
				(x += 1ll * b[i] * (i + 1) % mod) %= mod;
			else 
				(y += 1ll * b[i] * (i + 1) % mod) %= mod;
		}
		return x | (y << 32ll);
	}

	void init() {
		for (int i = 0; i < 6; ++i) {
			k[i] = 1; b[i] = 0;
		}
	}

}s1[maxn + 10], s2[maxn + 10];

data operator + (const data &a, const data &b) {
	data ans;
	for (int i = 0; i < 6; ++i) {
		ans.k[i] = 1ll * a.k[i] * b.k[i] % mod;
		ans.b[i] = (b.b[i] + 1ll * a.b[i] * b.k[i]) % mod;
	}
	return ans;
}

data inv(const data &a) {
	data ans;
	for (int i = 0; i < 6; ++i) {
		ans.k[i] = fpow(a.k[i], mod - 2);
		ans.b[i] = (mod - 1ll * ans.k[i] * a.b[i] % mod) % mod;
	}
	return ans;
}

