    if (s[0] == '<') pref0[0] = (pref0[0] * K) % MOD;
    if (s[0] == '<') pref02[0] = (pref02[0] * K2) % MOD;
    if (s[0] == '>') pref0[0] = (pref0[0] * rK) % MOD;
    if (s[0] == '>') pref02[0] = (pref02[0] * rK2) % MOD;
