        if (s[i] == '+') pref0[i] = (pref0[i - 1] + 1) % MOD;
        if (s[i] == '+') pref02[i] = (pref02[i - 1] + 1) % MOD;
        if (s[i] == '-') pref0[i] = (pref0[i - 1] + MOD - 1) % MOD;
        if (s[i] == '-') pref02[i] = (pref02[i - 1] + MOD - 1) % MOD;
        if (s[i] == '<') pref0[i] = (pref0[i - 1] * K) % MOD;
        if (s[i] == '<') pref02[i] = (pref02[i - 1] * K2) % MOD;
        if (s[i] == '>') pref0[i] = (pref0[i - 1] * rK) % MOD;
        if (s[i] == '>') pref02[i] = (pref02[i - 1] * rK2) % MOD;
        moves[i] = moves[i - 1];
        if (s[i] == '>') moves[i]++;
        if (s[i] == '<') moves[i]--;
    }
    for (int j = 0; j < n; j++)
    {
