long long int ppow(long long int i, long long int j, long long int MOD) {
	long long int ret = 1;
	while (j) {
		if (j & 1LL)ret *= i;
		if (ret >= MOD)ret %= MOD;
		j >>= 1LL;
	}
	return ret;
}

