ll powmod(ll a, ll e, ll mod) {
  ll sum = 1;
  ll cur = a;
  while (e > 0) {
    if (e % 2) {
      sum = sum * cur % mod;
    }
    cur = cur * cur % mod;
    e /= 2;
  }
  return sum;
}

const int A = 20;

int main(void) {
  const ll mod = 1e9 + 7;
  const ll mod2 = 1e9 + 9;
