  mt19937 mt;
  VL b(A);
  ll mods[A] = {};
  REP(i, 0, A) mods[i] = 1e9 + 7;

  REP(c, 0, A) {
    do {
      b[c] = mt() % mods[c];
    } while (b[c] == 0);
  }
  vector<VL> hsh(A, VL(n + 1));
  ll inv[A] = { 0 };
  REP(c, 0, A) inv[c] = powmod(b[c], mods[c] - 2, mods[c]);
  REP(c, 0, A) {
