      ll tmp = hsh[c][i];
      if (s[i] == '>') {
        p++;
        cur = cur * b[c] % mods[c];
      }
      if (s[i] == '<') {
        p--;
        cur = cur * inv[c] % mods[c];
      }
      if (s[i] == '+') {
        tmp = (tmp + cur) % mods[c];
      }
      if (s[i] == '-') {
        tmp = (tmp + mods[c] - cur) % mods[c];
      }
      hsh[c][i + 1] = tmp;
      pos[i + 1] = p;
    }
  }
  if (DEBUG) {
    REP(i, 0, n + 1) {
      cerr << i << " " << hsh[0][i] << endl;
    }
  }
  VL meguru(A);
  REP(c, 0, A) meguru[c] = hsh[c][n];
  map<VL, int> kirika;
  kirika[meguru] = 1;
  ll tot = 0;
  VL cur(A);
  REP(c, 0, A) cur[c] = powmod(b[c], pos[n], mods[c]);
  for (int i = n - 1; i >= 0; --i) {
    if (DEBUG) {
      DEBUGP(i);
      DEBUGP(pos[i]);
    }
    if (pos[i + 1] - pos[i] == 1) {
      REP(c, 0, A) cur[c] = cur[c] * inv[c] % mods[c];
    }
    else if (pos[i + 1] - pos[i] == -1) {
      REP(c, 0, A) cur[c] = cur[c] * b[c] % mods[c];
    }
    VL ken(A);
    REP(c, 0, A) ken[c] = (cur[c] * meguru[c] + hsh[c][i]) % mods[c];
    tot += kirika[ken];
    VL tt(A);
    REP(c, 0, A) tt[c] = hsh[c][i];
    kirika[tt] += 1;
  }
  cout << tot << endl;
}

