void add1(int &a,int b){
	a+=b;
	if(a >= M)a-=M1;
	if(a < 0)a+=M1;
}
int power(int base,int to){
	int ret = 1;
	while(to){
		if(to&1)ret = ret*1LL*base%M;
		to>>=1;
		base = base*1LL*base%M;
	}
	return ret;
}
int power1(int base,int to){
	int ret = 1;
	while(to){
		if(to&1)ret = ret*1LL*base%M1;
		to>>=1;
		base = base*1LL*base%M1;
	}
	return ret;
}
