typedef long long ll;

ll pow_mod(ll x,int k,ll MOD) {
  ll ans=1;
  while (k) {
  	if (k&1) ans=ans*x%MOD;
  	x=x*x%MOD;
  	k>>=1;
  }
  return ans;
}

const int prime[10]={998244353,998244353,998244353,998244353,998244353,998244353,998244353,998244353,998244353,998244353};
const ll H[10]={19260817,19260817,19260817,19260817,19260817,19260817,19260817,19260817,19260817,19260817};

int powd[10][1000005],invd[10][1000005];

void pre(int n) {
  for(int i=0;i<10;i++) {
    ll inv=pow_mod(H[i],prime[i]-2,prime[i]);
    powd[i][0]=1;
    for(int j=1;j<=n;j++) powd[i][j]=(ll)powd[i][j-1]*H[i]%prime[i];
    invd[i][0]=1;
    for(int j=1;j<=n;j++) invd[i][j]=(ll)invd[i][j-1]*inv%prime[i];
  }
}

struct Data {
  int num[10];
  Data() {memset(num,0,sizeof(num));}
  Data operator + (Data b) {
  	Data c;
  	for(int i=0;i<10;i++) c.num[i]=(num[i]+b.num[i])%prime[i];
  	return c;
  }
  void add(int x) {
  	for(int i=0;i<10;i++) num[i]=(num[i]+powd[i][x])%prime[i];
  }
  void dec(int x) {
  	for(int i=0;i<10;i++) num[i]=(num[i]-powd[i][x]+prime[i])%prime[i];
  }
  void rshift(int x) {
  	for(int i=0;i<10;i++) num[i]=(ll)num[i]*powd[i][x]%prime[i];
  }
  void lshift(int x) {
  	for(int i=0;i<10;i++) num[i]=num[i]*invd[i][x]%prime[i];
  }
  bool operator < (const Data & b) const {
  	for(int i=0;i<10;i++)
  	  if (num[i]!=b.num[i]) return num[i]<b.num[i];
  	return 0;
  }
};

map <Data,int> mp;
Data p[250005];

char str[250005];

int main() {
  int n;
  scanf("%d%s",&n,str+1);
  pre(4*n);
  int d=0;
  for(int i=1;i<=n;i++) {
  	p[i]=p[i-1];
    if (str[i]=='+') p[i].add(2*n+d);
    else if (str[i]=='-') p[i].dec(2*n+d);
    else if (str[i]=='>') d++;
    else d--;
    mp[p[i]]++;
  }
  ll ans=0;
  d=0;
  for(int i=1;i<=n;i++) {
  	Data t=p[n];
  	if (d>=0) t.rshift(d); else t.lshift(-d);
	t=t+p[i-1];
	if (mp.count(t)) ans+=mp[t];
	if (str[i]=='>') d++;
	else if (str[i]=='<') d--;
	mp[p[i]]--;
  }
  printf("%lld\n",ans);
  return 0;
}

