int q_pow(int x, int y, int mod) {
  int ans = 1;
  for(; y; y >>= 1) {
    if(y & 1) ans = 1ll * ans * x % mod;
    x = 1ll * x * x % mod;
  }
  return ans;
}

int h1[N], h2[N];
typedef pair<int, int> P;
map<P, int> mp;

int main() {
  int n;
  scanf("%d", &n);
  inv1 = q_pow(base1, mod1 - 2, mod1);
  inv2 = q_pow(base2, mod2 - 2, mod2);
  pw1[0] = pw2[0] = rev1[0] = rev2[0] = 1;
  for(int i = 1; i <= (n << 1); ++i) {
    pw1[i] = 1ll * pw1[i - 1] * base1 % mod1;
    pw2[i] = 1ll * pw2[i - 1] * base2 % mod2;
    rev1[i] = 1ll * rev1[i - 1] * inv1 % mod1;
    rev2[i] = 1ll * rev2[i - 1] * inv2 % mod2;
  }
  s[0] = n;
  for(int i = 1; i <= n; ++i) {
    char ch; cin >> ch;
    if(ch == '+') {
      s[i] = s[i - 1];
      h1[i] = (h1[i - 1] + pw1[s[i]]) % mod1;
      h2[i] = (h2[i - 1] + pw2[s[i]]) % mod2;
    } else if(ch == '-') {
      s[i] = s[i - 1];
      h1[i] = (h1[i - 1] - pw1[s[i]] + mod1) % mod1;
      h2[i] = (h2[i - 1] - pw2[s[i]] + mod2) % mod2;
    } else if(ch == '>') {
      s[i] = s[i - 1] + 1;
      h1[i] = h1[i - 1];
      h2[i] = h2[i - 1];
    } else {
      s[i] = s[i - 1] - 1;
      h1[i] = h1[i - 1];
      h2[i] = h2[i - 1];
    }
    mp[P(h1[i], h2[i])]++;
  }
  ll ans = 0;
  for(int i = 1; i <= n; ++i) {
    int pos = h1[n], tmp = h2[n];
    int sit = s[i - 1] - n;
    if(sit >= 0) pos = 1ll * pos * pw1[sit] % mod1, tmp = 1ll * tmp * pw2[sit] % mod2;
    else pos = 1ll * pos * rev1[-sit] % mod1, tmp = 1ll * tmp * rev2[-sit] % mod2;
    pos = (pos + h1[i - 1]) % mod1;
    tmp = (tmp + h2[i - 1]) % mod2;
    ans += mp[P(pos, tmp)];
    mp[P(h1[i], h2[i])]--;
  }
  printf("%lld\n", ans);
  return 0;
}

