        s[i] = c;
        if(c == '+') x++;
        if(c == '-') x--;
        if(c == '>') x *= X;
        if(c == '<') x *= bol;
        x = (x % mod + mod) % mod; 
    }

    a = 1; b = 0;
    M[0] = 1;
    for(i=n-1; i>=0 ;i--){
        c = s[i];
        if(c == '+') b -= a;
        if(c == '-') b += a;
        if(c == '>') a *= bol;
        if(c == '<') a *= X;
        a = (a % mod + mod) % mod;
        b = (b % mod + mod) % mod;
        t = (a*x + b) % mod;
        ans += M[t];
        M[b]++;
    }

    cout << ans;
    return 0;
}

