				int id = j * roots.size() + k;
				tr[i + 1][id] = tr[i][id] * roots[k] % mods[j];
				invtr[i + 1][id] = invtr[i][id] * invroots[id] % mods[j];
			}
		}
	}
}

void solve() {
	int n; cin >> n;
	string s; cin >> s;
	vector<ll> sum(mods.size()*roots.size(),0); int loc = 0;
	vector<vector<ll>> memo(n);
	rep(i, n)memo[i].resize(mods.size()*roots.size());
	vector<int> locs(n);
	int len = roots.size()*mods.size();
	rep(i, n) {
		if (s[i] == '>')loc++;
		else if (s[i] == '<')loc--;
		else if (s[i] == '+') {
			if (loc >= 0) {
				rep(j, len) {
					sum[j] += tr[loc][j];
				}
			}
			else {
				rep(j, len) {
					sum[j] += invtr[-loc][j];
				}
			}
		}
		else {
			if (loc >= 0) {
				rep(j, len) {
					sum[j] -= tr[loc][j];
				}
			}
			else {
				rep(j, len) {
					sum[j] -= invtr[-loc][j];
				}
			}
		}
		rep(j, len) {
			ll mod = mods[j / roots.size()];
			if (sum[j] < 0)sum[j] += mod;
			if (sum[j] >= mod)sum[j] -= mod;
			memo[i][j] = sum[j];
		}

		locs[i] = loc;
	}
	vector<ll> alls = sum;
	ll ans = 0;
	vector<map<ll, int>> mp(len);
	rep(i, len) {
		mp[i][alls[i]]++;
	}
	rep(i, n) {
		//cout << mp[memo[i]] << endl;
		int mi = 2*n;
		rep(j, len) {
			mi = min(mi, mp[j][memo[i][j]]);
		}
		ans += mi;
		bool valid = true;

		rep(j, len) {
			ll nex = alls[j];
			ll mod = mods[j / roots.size()];
			if (locs[i] >= 0) {
				nex = nex * tr[locs[i]][j] % mod;
			}
			else {
				nex = nex * invtr[-locs[i]][j] % mod;
			}
			nex += memo[i][j];
			if (nex >= mod)nex -= mod;
			mp[j][nex]++;
		}
	}
	cout << ans << endl;
}


signed main() {
	ios::sync_with_stdio(false);
	cin.tie(0);
	//cout << fixed << setprecision(7);
	init();
	solve();
	//stop
	return 0;
}

