	x=(x-=y)<0?x+mod:x;
}
struct node{
	int y[6];
	inline bool operator <(const node &rhs)const{
		for(ri i=0;i^6;++i)
			if(y[i]!=rhs.y[i])
				return y[i]<rhs.y[i];
		return 0;
	}
	inline void upd(ri p,ri k){
		ri i;
		if(p>=0){
			if(k==1){
				for(i=0;i^6;++i)
					add(y[i],power[i][p]);
			} 
			else{
				for(i=0;i^6;++i)
					del(y[i],power[i][p]);
			}
		}
		else{
			if(k==1){
				for(i=0;i^6;++i)
					add(y[i],powerinv[i][-p]);
			} 
			else{
				for(i=0;i^6;++i)
					del(y[i],powerinv[i][-p]);
			}
		}
	}
}res,res0,now;
struct orz{
	int k,b;
	inline void operator +=(const int &rhs){
		add(b,1);
	}
	inline void operator -=(const int &rhs){
		del(b,1);
	}
	inline void operator *=(const int &rhs){
		k=(ll)k*rhs%mod;
		b=(ll)b*rhs%mod;
	}
};
struct poly{
	orz f[6];
	inline void upd1(){
		for(ri i=0;i^6;++i)
			f[i]-=1;
	}
	inline void upd2(){
		for(ri i=0;i^6;++i)
			f[i]+=1;
	}
	inline void upd3(){
		for(ri i=0;i^6;++i)
			f[i]*=xinv[i];
	}
	inline void upd4(){
		for(ri i=0;i^6;++i)
			f[i]*=x[i];
	}
}resinv;
map<node,int> G;
inline int fpow(ri a,ri b){
	ri ans=1;
	for(;b;b>>=1,a=(ll)a*a%mod)
		if(b&1) ans=(ll)ans*a%mod;
	return ans;
}
