		xinv[i]=fpow(x[i],mod-2);
		powerinv[i][0]=1;
		for(j=1;j<=n;++j)
			powerinv[i][j]=(ll)powerinv[i][j-1]*xinv[i]%mod;
	}
	nowp=0;
	for(i=1;i<=n;++i)
		if(s[i]=='+')
			res0.upd(nowp,1);
		else if(s[i]=='-')
			res0.upd(nowp,-1);
