	for(int i = 1; i <= n; ++i) pow1[i] = pow1[i-1] * b1 % p1, pow2[i] = pow2[i-1] * b2 % p2;
	for(int i = -1; i >= -n; --i) pow1[i] = pow1[i+1]* pow1[-1] % p1, pow2[i] = pow2[i+1]* pow2[-1] % p2;
