inline __int128 mul(__int128 a,__int128 b){
	return (a*b)%mod2;
}
inline __int128 add(__int128 a,__int128 b){
	auto f=a+b;
	if(f>=mod2)f%=mod2;
	return f;
}
inline __int128 sub(__int128 a,__int128 b){
	auto f=a-b;
	if(f<0)f+=mod2;
	f%=mod2;
	return f;
}
__int128 ppow(__int128 a,__int128 b){
	__int128 res=1;
	while(b){
		if(b&1LL)res*=a;
		if(res>=mod2)res%=mod2;
		a*=a;
		a%=mod2;
		b>>=1LL;
	}
	return res;
}
unordered_map<long long int,int> cnt;
vector<pair<__int128,__int128> > v;
