inline LL fpm(LL x, LL y)
{
	LL res = 1;
	for(; y; y >>= 1, x = (__int128) x * x % MOD) if(y & 1) res = (__int128) res * x % MOD;
	return res;
}

inline void solve()
{
	static LL p[MAXN + 5], prefix[MAXN + 5], p_pow[MAXN * 2 + 5];

	p_pow[0 + n] = 1;
	for(int i = 1, invB = fpm(B, MOD - 2); i <= n; ++i)
	{
		p_pow[ i + n] = (__int128) p_pow[ i - 1 + n] *	B % MOD;
		p_pow[-i + n] = (__int128) p_pow[-i + 1 + n] * invB % MOD;
	}

	for(int i = 1; i <= n; ++i)
	{
		p[i] = p[i - 1], prefix[i] = prefix[i - 1];
		if(s[i] == '<') --p[i];
		if(s[i] == '>') ++p[i];
		if(s[i] == '+') (prefix[i] += p_pow[p[i] + n]) %= MOD;
		if(s[i] == '-') (prefix[i] -= p_pow[p[i] + n]) %= MOD;
	}
	for(int i = 1; i <= n; ++i) (prefix[i] += MOD) %= MOD;

	static unordered_map<LL, int> cnt;

	LL ans = 0;
	for(int i = 1; i <= n; ++i) ans += prefix[i] == prefix[n];
	for(int i = 1; i <= n; ++i)
	{
		ans += cnt[prefix[i]];
		LL res = (prefix[i] + (__int128) p_pow[p[i] + n] * prefix[n] % MOD) % MOD;
		++cnt[res];
	}
	printf("%lld\n", ans);
}

