int fp1(int a, int p) {
    int ans = 1, c = a;
    for (int i = 0; (1ll << i) <= p; ++i) {
        if ((p >> i) & 1) ans = mod1(ans * c);
        c = mod1(c * c);
    }   
    return ans;
}   
int dv1(int a, int b) {
    return mod1(a * fp1(b, MOD1 - 2));
}   
int fp2(int a, int p) {
    int ans = 1, c = a;
    for (int i = 0; (1ll << i) <= p; ++i) {
        if ((p >> i) & 1) ans = mod2(ans * c);
        c = mod2(c * c);
    }   
    return ans;
