        else {
            w.first = mod1(pref1[i] + mod1(pref1[n] * pw1[x[i]]));
            w.second = mod2(pref2[i] + mod2(pref2[n] * pw2[x[i]]));
        }
        ans += d[w];
        d[{pref1[i], pref2[i]}]++;
    }   
    cout << ans << '\n';
}

