typedef long long lint;

lint modpow(lint x, lint y, lint mod) {
  lint ret = 1 % mod, x2p = x;
  while (y > 0) {
    if (y % 2) ret = (__int128)ret * x2p % mod;
    y /= 2;
    x2p = (__int128)x2p * x2p % mod;
  }
  return ret;
}

constexpr int nmods = 15;

constexpr lint mod[] = {1152921504609811141LL, 576460752312103501LL,
                        4611676018431352883LL, 4611676018431352883LL,
                        288230376151711789,288230376151711793,288230376151711813,288230376151711843,288230376151711849,288230376151711879,288230376151711909,288230376151711919,288230376151711921,288230376151711933,288230376151711951};

