void enter() {
	cin >> n >> s;
}

int64_t pw(int n, int k) {
	if (k==0)
		return 1;
	int64_t tmp = pw(n, k/2);
	if (k%2)
		return tmp * tmp % MOD * n % MOD;
	return tmp * tmp % MOD;
}

void init_pref() {
	int p = offset;
	int64_t H = 0;
	for (int i=0; i<n; ++i) {
		if (s[i]=='<' || s[i]=='>') {
			p += (s[i]=='<' ? -1 : 1);
			cnt[i] = cnt[i-1] + (s[i]=='<' ? -1 : 1);
		}
		else {
			H = (H - pw(T, p) * (s[i]=='-' ? 1 : -1)) % MOD;
		}
		pref_hash[i] = (H + MOD) % MOD;
	}
}

void init_suff() {
	int64_t H = pref_hash[n-1];
	int p = offset;
	for (int i=0; i<n; ++i) {
		if (s[i]=='>' || s[i]=='<')
			p += (s[i]=='>' ? 1 : -1);
		else {
			// debug(i);
			// debug(p);
			// debug((s[i]=='+' ? -1 : 1));
			H = (H + pw(T, p) * (s[i]=='+' ? -1 : 1)) % MOD;
		}
		suff_hash[i+1] = (H + MOD) % MOD;
	}
}

int64_t solve() {
	map<int64_t, int> mp;
	int64_t H = pref_hash[n-1];
	int64_t res = 0;
	for (int j=0; j<n; ++j) {
		if (j==0)
			++mp[0];
		else
			++mp[((H - pref_hash[j-1] - H * pw(T, cnt[j-1])) % MOD + MOD) % MOD];
		res += mp[suff_hash[j+1]];
	}
	return res;
}

