		if (s[i]=='<') p3[i]=mul3(p3[i],ibas3);
		if (s[i]=='>') p3[i]=mul3(p3[i],bas3);
	}

	for (int i=n;i;i--) {
		m[mp(mp(h1[i],h2[i]),h3[i])]++;
		ll t1=add1(mul1(h1[n],p1[i-1]),h1[i-1]),t2=add2(mul2(h2[n],p2[i-1]),h2[i-1]),t3=add3(mul3(h3[n],p3[i-1]),h3[i-1]);
		ans+=m[mp(mp(t1,t2),t3)];
	}
	printf("%lld\n",ans);
	return 0;
}

