const ll MOD=1e9+9;
const int med=250000;
ll powmod(ll a, ll k){
    ll ap=a, ans=1;
    while(k>0){
        if(k%2==1){
            ans*=ap;
            ans%=MOD;
        }
        ap=ap*ap;
        ap%=MOD;
        k/=2;
    }
    return ans;
}
ll inv(ll a){
	return powmod(a, MOD-2);
}

int main()
{
	int n;
	cin>>n;
	string s; cin>>s;
	ll b=998244353;
	ll binv=inv(b);
	ll bp[500001];
	bp[med]=1;
	for(int i=med+1; i<=med+n; i++) bp[i]=bp[i-1]*b%MOD;
	for(int i=med-1; i>=med-n; i--) bp[i]=bp[i+1]*binv%MOD;
	ll b0=0; int p=med;
	for(int i=0; i<n; i++){
		if(s[i]=='+'){
			b0=(b0+bp[p])%MOD;
		}else if(s[i]=='-'){
			b0=(b0+MOD-bp[p])%MOD;
		}else if(s[i]=='>'){
			p++;
		}else{
			p--;
		}
	}
	ll ans=0;
	ll x[250000]={};
	unordered_map<ll, int> mp[500];
	int c1[500]={};
	ll c0[500]={};
	int sq=2000;
	for(int i=0; i<n; i++){
		if(s[n-1-i]=='+'){
			for(int j=i/sq*sq; j<=i; j++){
				x[j]=(x[j]+1)%MOD;
			}
			for(int j=0; j<i/sq; j++){
				c0[j]=(c0[j]+1)%MOD;
			}
		}else if(s[n-1-i]=='-'){
			for(int j=i/sq*sq; j<=i; j++){
				x[j]=(x[j]+MOD-1)%MOD;
			}
			for(int j=0; j<i/sq; j++){
				c0[j]=(c0[j]+MOD-1)%MOD;
			}
		}else if(s[n-1-i]=='>'){
			for(int j=i/sq*sq; j<=i; j++){
				x[j]=x[j]*b%MOD;
			}
			for(int j=0; j<i/sq; j++){
				c1[j]++;
				c0[j]=c0[j]*b%MOD;
			}
		}else{
			for(int j=i/sq*sq; j<=i; j++){
				x[j]=x[j]*binv%MOD;
			}
			for(int j=0; j<i/sq; j++){
				c1[j]--;
				c0[j]=c0[j]*binv%MOD;
			}
		}
		for(int j=0; j<i/sq; j++){
			ll x0=(b0-c0[j]+MOD)*bp[med-c1[j]]%MOD;
			auto itr=mp[j].find(x0);
          if(itr!=mp[j].end()) ans+=((ll)(itr->second));
		}
		for(int j=i/sq*sq; j<=i; j++){
			if(b0==x[j]) ans++;
		}
		if((i+1)%sq==0){
			for(int j=i/sq*sq; j<=i; j++){
				mp[i/sq][x[j]]++;
			}
		}
	}
	cout<<ans<<endl;
	return 0;
}

