struct mll{
	static ll MOD;
	ll val;
	mll(ll v = 0): val(v % MOD){ if (val < 0) val += MOD; }
	mll operator - () const { return -val; }
	mll operator + (const mll &b) const { return val + b.val; }
	mll operator - (const mll &b) const { return val - b.val; }
	mll operator * (const mll &b) const { return val * b.val; }
	mll operator / (const mll &b) const { return mll(*this) /= b; }
	mll operator + (ll b) const { return *this + mll(b); }
	mll operator - (ll b) const { return *this - mll(b); }
	mll operator * (ll b) const { return *this * mll(b); }
	friend mll operator + (ll a, const mll &b) { return b + a; }
	friend mll operator - (ll a, const mll &b) { return -b + a; }
	friend mll operator * (ll a, const mll &b) { return b * a; }
	friend mll operator / (ll a, const mll &b) { return mll(a)/b; }
	mll &operator += (const mll &b) { val=(val+b.val)%MOD; return *this; }
	mll &operator -= (const mll &b) { val=(val+MOD-b.val)%MOD; return *this; }
	mll &operator *= (const mll &b) { val=(val*b.val)%MOD; return *this; }
	mll &operator /= (const mll &b) {
		ll c=b.val, d=MOD, u=1, v=0;
		while (d){
			ll t = c / d;
			c -= t * d; swap(c, d);
			u -= t * v; swap(u, v);
		}
		val = val * u % MOD;
		if (val < 0) val += MOD;
		return *this;
	}
	mll &operator += (ll b) { return *this += mll(b); }
	mll &operator -= (ll b) { return *this -= mll(b); }
	mll &operator *= (ll b) { return *this *= mll(b); }
	mll &operator /= (ll b) { return *this /= mll(b); }
	bool operator == (const mll &b) { return val == b.val; }
	bool operator != (const mll &b) { return val != b.val; }
	bool operator == (ll b) { return *this == mll(b); }
	bool operator != (ll b) { return *this != mll(b); }
	friend bool operator == (ll a, const mll &b) { return mll(a) == b.val; }
	friend bool operator != (ll a, const mll &b) { return mll(a) != b.val; }
	friend ostream &operator << (ostream &os, const mll &a) { return os << a.val; }
	friend istream &operator >> (istream &is, mll &a) { return is >> a.val; }
	static mll Combination(ll a, ll b){
		chmin(b, a-b);
		if (b<0) return mll(0);
		mll c = 1;
		REP(i, b) c *= a-i;
		REP(i, b) c /= i+1;
		return c;
	}
};
ll mll::MOD = 2099999999LL;// 998244353LL;//1000000007LL;
using vmll = std::vector<mll>;
using vvmll = std::vector<vmll>;
using vvvmll = std::vector<vvmll>;
using vvvvmll = std::vector<vvvmll>;
using vvvvvmll = std::vector<vvvvmll>;

using Data = tuple<mll, ll, mll>;

Data prod(Data a, Data b){
	mll ha; ll pa; mll qpa; tie(ha, pa, qpa) = a;
	mll hb; ll pb; mll qpb; tie(hb, pb, qpb) = b;
	return Data(ha+qpa*hb, pa+pb, qpa*qpb);
}

void solve()
{
	ll N; string S;  cin >> N >> S;
	mll q = 999983;
	mll qinv = mll(1) / q;

	Data plus  = Data( 1, 0, 1);
	Data minus = Data(-1, 0, 1);
	Data right = Data( 0, 1, q);
	Data left  = Data( 0,-1, qinv);

	//W
	Data W ={0,0,1};
	DEPS(i, 0, N-1){
		Data si;
		if      (S[i] == '+') si = plus;
		else if (S[i] == '-') si = minus;
		else if (S[i] == '>') si = right;
		else                  si = left;
		W = prod(si, W);
	}
	mll hW = get<0>(W);

	unordered_map<ll, ll> CinvMap;
	Data X ={0,0,1};
	Data Xinv ={0,0,1};
	ll ans = 0;
	DEPS(i, 0, N-1){
		//XinvCinv
		ll h = get<0>(Xinv).val;
		CinvMap[h]++;
		//X
		Data si;
		if      (S[i] == '+') si = plus;
		else if (S[i] == '-') si = minus;
		else if (S[i] == '>') si = right;
		else                  si = left;
		X = prod(si, X);
		//Xinv
		Data siinv;
		if      (S[i] == '+') siinv = minus;
		else if (S[i] == '-') siinv = plus;
		else if (S[i] == '>') siinv = left;
		else                  siinv = right;
		Xinv = prod(Xinv, siinv);

		//C^-1h
		mll hX = get<0>(X);
		mll qpXinv = get<2>(Xinv);
		mll hCinv = (hW-hX) * qpXinv;
		if (CinvMap.count(hCinv.val) == 1){
			ans += CinvMap[hCinv.val];
		}
	}
	cout << ans << '\n';
}


int main(){
	solve();
	return 0;
}

