	static long ans = 0;

	static void dfs(int l, int r, char[] s, RollingHashFactory rhf, long HU, long HL)
	{
		if(l >= r)return;
		int h = l+r>>1;
		// h(2)
		long rhu = 0, rhl = 0;
		long[] hits = new long[r-h];
		{
