            long ans = 0;
            Map<Long, Integer> mp = new HashMap<>();
            long[][] v0 = transpose(new long[][]{new long[]{0, 0, 1}});
            long[][] vc = transpose(new long[][]{new long[]{c, 0, 1}});
            for (int i = 0; i <= n; i++) {
