        long[][] getMat(char c) {
            if (c == '+') {
                return new long[][]{
                        new long[]{1, 0, 0},
                        new long[]{0, 1, 1},
                        new long[]{0, 0, 1},
                };
            }
            if (c == '-') {
                return new long[][]{
                        new long[]{1, 0, 0},
                        new long[]{0, 1, -1},
                        new long[]{0, 0, 1},
                };
            }
            if (c == '<') {
                return new long[][]{
                        new long[]{X, X, 0},
                        new long[]{0, 0, 0},
                        new long[]{0, 0, 1},
                };
            }
            if (c == '>') {
                return new long[][]{
                        new long[]{invX, invX, 0},
                        new long[]{0, 0, 0},
                        new long[]{0, 0, 1},
                };
            }
