        static long[][] transpose(long[][] mat) {
            long[][] res = new long[mat[0].length][mat.length];
            for (int i = 0; i < mat.length; i++) {
                for (int j = 0; j < mat[i].length; j++) {
                    res[j][i] = mat[i][j];
                }
            }
            return res;
        }

        static long[][] mulmat(long[][] a, long[][] b, int mod) {
            final long BIG = (2L * mod) * (2L * mod);

            assert (a[0].length == b.length);

            final int n = a.length;
            final int v = b.length;
            final int m = b[0].length;

            long[][] res = new long[n][m];
            for (int i = 0; i < n; i++)
                for (int k = 0; k < v; k++) {
                    final long aa = a[i][k];
                    for (int j = 0; j < m; j++) {
                        res[i][j] += aa * b[k][j];
                        if (res[i][j] >= BIG) res[i][j] -= BIG;
                    }
                }
            for (int i = 0; i < n; i++)
                for (int j = 0; j < m; j++)
                    res[i][j] %= mod;

            return res;
        }

        static long powmod(long n, long r, int m) {
            long res = 1;
            for (; r != 0; r >>>= 1, n = n * n % m) {
                if ((r & 1) == 1) {
                    res = res * n;
                    if (res >= m) {
                        res %= m;
                    }
                }
            }
            return res;
        }

    }

